/* Reset some default styles */
html {
    font-size: 62.5%;
    margin: 0;
    padding: 0;
}

body {
    font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f5f5f5;
}

/* Header */
.header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background-color: #66c0ec;
    width: 100%;
    height: 5.5rem;
    display: flex;
    align-items: center;
}

.header__content {
    font-family: 'Paytone One', sans-serif;
    text-align: left;
    font-size: 3.5rem;
    margin: 0;
    margin-left: .5rem;
    color: #000;
}

/* Sidebar */
.sidebar {
    position: fixed;
    background-color: #66c0ec;
    display: flex;
    flex-direction: row;
    height: calc(100vh - 5.5rem);
    top: 5.5rem; /* Position below the header */
    width: 5rem;
    transition: width .5s;
    overflow: hidden;
    margin-top: -.5rem;
    z-index: 2;
}

.sidebar:hover {
    position: absolute;
    width: 15rem;
    z-index: 2; /* Bring sidebar to the front when expanded */
}


.sidebar__list {
    padding:0;
}

.sidebar__selection {
    display: flex;
    align-items: center;
    padding: 1.6rem 0;
    font-size: 1.4rem;
    color: black;
    transition: width .5s;
    font-weight: 600;
}

.sidebar__selection:hover {
    background-color: lightslategray;
    min-width: 15rem;
}

.sidebar__icon {
    width: 4rem;
    text-align: center;
    margin-right: 1rem;
    margin-left: .3rem;
    font-size: 2rem;
}


.sidebar__text {
    text-decoration: none;
    color: inherit;
}

.active--link {
    color: darkblue;
  }

/* Reset some default styles */
html {
    font-size: 62.5%;
    margin: 0;
    padding: 0;
}

/* Conent Block */

.main__block {
    position: absolute;
    top: 6.5rem; /* Set the top position below the header */
    left: 6.5rem; /* Adjust the left position for spacing */
    right: 0;
    bottom: 0;
    overflow-y: scroll;
    height: calc(100vh - 6.5rem); /* Set a fixed height based on viewport height */
}

.content__block {
    display: flex;
    gap: 20px;
    margin: 15px 0px 15px 0px;

}

/* Table */
.table__container {
    width: 85vh;
    height: 85vh;
    background-color: #fff;
    border: 1px solid #ccc;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.5);
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    border-radius: 10px;
}

.table__title {
    margin-top: 10px;
    font-size: 1.5rem; 
    font-weight: bold;
    color: #000;
}

/* Collapsible */
.collapsible__block {
    display: flex;
    width: 100%;
    gap: 20px; 
}


.collapsible__container {
    position: relative;
    border: 1px solid #ccc;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
    border-radius: 10px;
    display: flex; /* Use flexbox */
    max-width: 20rem;
    max-height: 7.5rem;
    background-color: #fff;
}

.collapsible__header {
    display: flex;
    flex-direction: column;
    padding: 5px 20px 0px 20px;
    white-space: nowrap;
}

.collapsible__heading {
    margin: 0;
    font-size: 1.2rem;
    color: #000;
}

.collapsible__chevron {
    transition: transform 0.3s;
    transform: rotate(180deg);
}

.collapsible--expanded .collapsible__chevron {
    transform: rotate(0);
}

.collapsible__selection {
    font-size: 1.4rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    pointer-events: auto;
    max-width: 20rem;
    min-width: 15rem;
    color: #000;
}

/* Create and style the line */
.collapsible__selection::before {
    content: "";
    position: absolute;
    bottom: 10px;
    left: 15px;
    right: 15px;
    height: 1px; /* Adjust the height of the line as needed */
    background-color: #000; /* Line color */
}

.collapsible--expanded .collapsible__content {
    max-height: 40vh;
    opacity: 1;
    pointer-events: auto;
    z-index: 1;
    color: #000;
}

.collapsible__content {
    position: relative;
    list-style: none;
    padding: 5px;
    background-color: #fff;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
    opacity: 0;
    transition: all .3s;
    font-size: 1.4rem;
    border-radius: 5px;
    margin: .5rem 1rem 0rem 1rem;
    overflow-y: auto;
}

.collapsible__list {
    position: relative;
    padding: 5px;
}

.collapsible__list:hover::before {
    content: "";
    position: absolute;
    top: 0px; /* Adjust to match your margin-top value */
    right: -6px;
    bottom: 0px; /* Adjust to match your margin-bottom value */
    left: -6px;
    background-color: lightgray;
    z-index: -1;
}

/* Map */


.map__container {
    width: 85vh;
    height: 85vh;
    background-color: #fff;
    border: 1px solid #ccc;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.5);
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    border-radius: 10px;
}

.map__title {
    margin-top: 10px;
    font-size: 1.5rem; 
    font-weight: bold;
    min-width: 350px;
    color: #000;
}

.map {
    width: 95%;
    height: 92%;
    border: 1px solid #ccc;
    border-radius: 5px;
    z-index: 0;
}

/* Weather Charts */


.chart__container {
    width: 75vh;
    height: 45vh;
    background-color: #fff;
    border: 1px solid #ccc;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.5);
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    border-radius: 10px;
    margin: 20px;
    border: 1px solid #ccc;
    border-radius: 5px;
}

.chart__container__bottom {
    position: relative;
    align-items: center;
    justify-content: center;
    width: 100vh;
    height: 65vh;
    background-color: #fff;
    border: 1px solid #ccc;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.5);
    border-radius: 10px;
    margin: 20px;
}

.top__section {
    display: flex;
    justify-content: flex-start;
    align-items: center;
}

.line__chart {
    width: 70vh;
    height: 38vh;
}

.pie__chart {
    width: 60vh;
    height: 35vh;
}

.weather__chart {
    width: 140vh;
    height: 38vh;
}



/* Style the section headers for the charts */
.section__header {
  margin-bottom: 20px;
  color: #333;
  font-size: 20px; /* Adjust the font size as needed */
  font-weight: bold; /* Make the text bold */
  text-align: center; /* Center the section header text */
}
